import React, { Component } from 'react';
import './App.css';
import Weather from './component/weather';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'weather-icons/css/weather-icons.css';
import Form from './component/form.component.jsx';

//api.openweathermap.org/data/2.5/weather?q=London,uk&appid={API key}
const APIkey="266ee41fcb7c9137df50b0d49662e958";
// ---------------------------------------------------------------------------
class WeatherLater extends Component {
  render() {
    return (
      <section className="rest-of-today-weather">
        {this.props.dataLater.slice(1, 8).map((dataLater, index) => {
          return (
            <div>
              <p className="hour">{new Date(dataLater.dt * 1000).getHours()}:00</p>
              <img width="100px" src={this.getImage(dataLater.weather[0].id)} alt="" />
              <p className="temperature">{(dataLater.main.temp - 273.15).toFixed(2)}ÂºC</p>
            </div>
          )
        })}
      </section>
    );
      }}
// -----------------------------------------------------------------------
class App extends React.Component{
  constructor(){
    super();
    this.state={
      city:undefined,
      country:undefined,
      icon:undefined,
      main:undefined,
      celsius:undefined,
      temp_max:undefined,
      temp_min:undefined,
      description:"",
      humidity:undefined,
      pressure:undefined,
      error:false
    };


    this.weatherIcon={
      Thunderstorm:"wi-thunderstorm",
      Drizzle:"wi-sleet",
      Rain:"wi-storm-showers",
      Snow:"wi-snow",
      Atomosphere:"wi-fog",
      Clear:"wi-day-sunny",
      Clouds:"wi-day-fog"
    }
  }

calcCelsius(temp){
  let cell=Math.floor(temp-273.15);
  return cell;
}

get_WeatherIcon(icons,rangeid){
  switch(true){
    case rangeid>=200&rangeid<=232:
      return this.setState({icon:this.weatherIcon.Thunderstorm});

      case rangeid>=300&rangeid<=321:
      return this.setState({icon:this.weatherIcon.Drizzle});

       case rangeid>=500&rangeid<=531:
        return this.setState({icon:this.weatherIcon.Rain});

       case rangeid>=600&rangeid<=622:
        return this.setState({icon:this.weatherIcon.Snow});

       case rangeid>=701&rangeid<=781:
        return this.setState({icon:this.weatherIcon.Atomosphere});

       case rangeid===800:
        return this.setState({icon:this.weatherIcon.Clear});

       case rangeid>=800&rangeid<=804:
        return this.setState({icon:this.weatherIcon.Clouds});

      default:
        return this.setState({icon:this.weatherIcon.Clouds});
  }
}

  getWeather=async(e)=>{
    e.preventDefault();

    const city=e.target.elements.city.value;
    const country=e.target.elements.country.value;

    //'http://api.openweathermap.org/data/2.5/weather?q=London,uk&appid='+APIkey);
    if(city && country)
    {
    const api_call= await fetch(
      `http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=`+APIkey);
    const response=await api_call.json();
    console.log(response);
    this.setState({
      // city:response.name,
      city:response.name,
      country:response.sys.country,
      celsius:this.calcCelsius(response.main.temp),
      temp_max:this.calcCelsius(response.main.temp_max),
      temp_min:this.calcCelsius(response.main.temp_min),
      description:response.weather[0].description,
      humidity:response.main.humidity,
      pressure:response.main.pressure,
      error:false

    });
    this.get_WeatherIcon(this.weatherIcon,response.weather[0].id);
  }else{
    this.setState({error:true})
  }
  }
  render(){
    return(
      <div className="App">
        <Form loadweather={this.getWeather} error={this.state.error}/>

        <Weather 
        city={this.state.city} 
        country={this.state.country} 
        temp_celsius={this.state.celsius}
        temp_max={this.state.temp_max}
        temp_min={this.state.temp_min}
        description={this.state.description}
        weatherIcon={this.state.icon}
        humidity={this.state.humidity}
        pressure={this.state.pressure}


        />
      </div>
    );
  }
}






export default App;
