import React, { Component } from 'react';

// import React from 'react';
class Hourlyweather extends Component{
    constructor(props){
        super(props);
            this.state={

                input:""
            };
        }
            //-------------------
            get_WeatherIcon(icons,rangeid){
                switch(true){
                  case rangeid>=200&&rangeid<=232:
                    return this.setState({icon:this.weatherIcon.Thunderstorm});

                    case rangeid>=300&&rangeid<=321:
                    return this.setState({icon:this.weatherIcon.Drizzle});

                     case rangeid>=500&&rangeid<=531:
                      return this.setState({icon:this.weatherIcon.Rain});

                     case rangeid>=600&&rangeid<=622:
                      return this.setState({icon:this.weatherIcon.Snow});

                     case rangeid>=701&&rangeid<=781:
                      return this.setState({icon:this.weatherIcon.Atomosphere});

                     case rangeid===800:
                      return this.setState({icon:this.weatherIcon.Clear});

                     case rangeid>=800&&rangeid<=804:
                      return this.setState({icon:this.weatherIcon.Clouds});

                    default:
                      return this.setState({icon:this.weatherIcon.snow});
                }
              }
            //-------------------

    render (){
         return(
            <div>
                { //${this.props.weatherIcon} 
                    this.props.hours.map(lsit=>
                        <li>
                        <div>
                            <h3>{new Date(lsit.dt_txt).getHours().toString() + ":00"}</h3>
                            {/* --------- */}
                            <h5 className="py-2">
                            <i className={`wi ${this.props.get_WeatherIcon(this.weatherIcon,lsit.weather[0].id)} display-1`}/>

                            {/* <i className={`wi ${props.weatherIcon} display-6`}/> */}
                             </h5>
                            {/* --------- */}
                            <h3>{lsit.main.temp} <span>&deg;</span></h3>
                        </div>
                        </li>
                        )
                }

            </div>
         )       
    }
}
export default Hourlyweather;



